import React, { Component } from 'react';
//import logo from './logo.svg';
//import './App.css';
import firebase from "firebase";

const config = {
      apiKey: "AIzaSyCbRZwXF2eXe2DpLNeWBDW8N4I8WDOVge8",
      authDomain: "snap-vote-77029.firebaseapp.com",
      databaseURL: "https://snap-vote-77029.firebaseio.com",
      projectId: "snap-vote-77029",
      storageBucket: "snap-vote-77029.appspot.com",
      messagingSenderId: "332977921881"
};



var data = firebase.database();
var num = 0;

class Users extends Component {
 constructor() {
   super();
   this.state = {
     username: '',
     currentItem: '',
     picture: localStorage.getItem("userSnap"),
     items: []
   }
   this.handleChange = this.handleChange.bind(this);
   this.handleSubmit = this.handleSubmit.bind(this);
 }
 handleSubmit(e) {
   e.preventDefault();
   const itemsRef = firebase.database().ref('items');
   const item = {
     title: this.state.currentItem,
     user: this.state.username,
     pic: this.state.picture
   }
   itemsRef.push(item);
   this.setState({
     currentItem: '',
     username: ''
   });
   itemsRef.on('value', (snapshot) => {
     console.log(snapshot.val());
   });
 }
 handleChange(e) {
   this.setState({
     [e.target.name]: e.target.value
   });
 }
 componentDidMount() {
   const itemsRef = firebase.database().ref('items');
   itemsRef.on('value', (snapshot) => {
     let items = snapshot.val();
     let newState = [];
     for (let item in items) {
       newState.push({
         id: item,
         title: items[item].title,
         user: items[item].user,
         pic: items[item].pic
       });
     }
     this.setState({
       items: newState
     });
   });
 }
 render() {
   return (
     <div className='app'>
       <header>
           <div className='wrapper'>
             <h1>Snap Vote</h1>
           </div>
       </header>
       <div className='container'>
         <section className="add-item">
         <p>Vims or Emacs? </p>
           <form onSubmit={this.handleSubmit}>
             <input type="text" name="currentItem" placeholder="Why? (optional)" onChange={this.handleChange} value={this.state.currentItem} />
             <p2>You can only vote once!</p2>
             <button>Send</button>
           </form>
         </section>
         <section className='display-item'>
           <div className="wrapper">
             <ul>
               {this.state.items.map((item) => {
                 return (
                   <li key={item.id}>
                     <h3>{item.title}</h3>
                     <p>by: {item.user}</p>
                   </li>
                 )
               })}
             </ul>
           </div>
         </section>
       </div>
     </div>
   );
 }
}
export default Users;





